const Dao = require("./dao.js");

module.exports = class ArticleDao extends Dao {
    getAll(callback) {
        super.query("Select id, tittel, tekst, bilde, forfatter, viktighet, kategoriid, alt from artikkel where viktighet = 1 order by tidspunkt desc", [], callback);
    }

    getNyheter(callback) {
        super.query("Select id, tittel, tekst, bilde, forfatter, viktighet, kategoriid, alt from artikkel where tidspunkt >= DATE_SUB(NOW(), INTERVAL 1 DAY) order by tidspunkt desc", [], callback);
    }
    getSiste(callback) {
        super.query("Select id, tittel, tekst, bilde, forfatter, viktighet, kategoriid, alt from artikkel where tidspunkt >= DATE_SUB(NOW(), INTERVAL 6 hour ) and viktighet = 1 order by tidspunkt desc", [], callback);
    }

    getSport(callback) {
        super.query("Select id, tittel, tekst, bilde, forfatter, viktighet, kategoriid, alt from artikkel where kategoriid = 2 order by tidspunkt desc", [], callback);
    }

    getTeknologi(callback) {
        super.query("Select id, tittel, tekst, bilde, forfatter, viktighet, kategoriid, alt from artikkel where kategoriid = 3 order by tidspunkt desc", [], callback);
    }

    getKultur(callback) {
        super.query("Select id, tittel, tekst, bilde, forfatter, viktighet, kategoriid, alt from artikkel where kategoriid = 4 order by tidspunkt desc", [], callback);
    }

    getCategories(callback) {
        super.query("Select id, navn from kategori", [], callback);
    }

    getOne(id, callback) {
        super.query(
            "Select id, tittel, tekst, bilde, forfatter, viktighet, kategoriid, alt from artikkel where id=?",
            [id],
            callback
        );
    }

    createOne(Artikkel, callback) {
        var val = [Artikkel.tittel, Artikkel.tekst, Artikkel.bilde, Artikkel.forfatter, Artikkel.viktighet, Artikkel.kategoriid, Artikkel.alt];
        super.query(
            "insert into artikkel (tittel, tekst, bilde, forfatter, viktighet, kategoriid, alt) values (?,?,?,?,?,?,?)",
            val,
            callback
        );
    }

    deleteOne(id, callback) {
        super.query(
            "delete from artikkel where id = ?",
            id,
            callback
        )
    }

    updateOne(Artikkel, callback) {
        var val = [Artikkel.tittel, Artikkel.tekst, Artikkel.bilde, Artikkel.forfatter, Artikkel.viktighet, Artikkel.kategoriid, Artikkel.alt, Artikkel.id];
        super.query(
            "update artikkel set tittel = ?, tekst = ?, bilde = ?, forfatter = ?, viktighet = ?, kategoriid = ?, alt = ? where id = ?",
            val,
            callback
        );
    }
};

